import React from 'react';
import { useState } from 'react';
import axios from 'axios';
import sun from './assets/sun.mp4'
function App() {
  const [data, setData] = useState({})
  const [location, setLocation] = useState('')

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=895284fb2d2c50a520ea537456963d9c`


  function currentTime(timezoneIn, dtIn) {
    let dateTime = new Date(dtIn * 1000 + (timezoneIn * 1000));

    // Convert into 24-hour format
    let hour = (dateTime.getHours() % 12) - 3;
    let ampm = hour >= 12 ? 'pm' : 'am';

    let minutes = dateTime.getMinutes();
    let weekday = dateTime.toLocaleString('default', { weekday: 'long' });
    let month = dateTime.toLocaleString('default', { month: 'short' });
    let date = dateTime.getDate();

    return `${hour} : ${minutes} ${ampm} - ${weekday} , ${month} ${date}`; 
  }


  const searchLocation = (event) => {
    if (event.key === 'Enter') {
      axios.get(url).then((response) => {
        setData(response.data)
        console.log(response.data)
      })

      setLocation('')
    }
  }

  return (
    <div className="app">
      <video className='video-bg' id='background' autoPlay loop muted>
        <source src={sun} type='video/mp4' />  
      </video>

      <div className="search">
        <input
          value={location}
          onChange={event => setLocation(event.target.value)}
          onKeyPress={searchLocation}
          placeholder='Enter Location'
          type="text" />
      </div>

      <div className="container">
        <div className="top">
          <div className="location">
            <p>{data.name}</p>
          </div>
          <div className="temp">
            {data.main ? <h1>{data.main.temp.toFixed()}°F</h1> : null}
          </div>
          <div className="description">
            {data.weather ? <p>{data.weather[0].main}</p> : null}
          </div>
        </div>

        {data.name !== undefined &&
          <div className="bottom">
            <div className="feels">
              {data.main ? <p className='bold'>{data.main.feels_like.toFixed()}°F</p> : null}
              <p>Feels Like</p>
            </div>
            <div className="humidity">
              {data.main ? <p className='bold'>{data.main.humidity}%</p> : null}
              <p>Humidity</p>
            </div>
            <div className="wind">
              {data.wind ? <p className='bold'>{data.wind.speed.toFixed()} MPH</p> : null}
              <p>Wind Speed</p>
            </div>
          </div>
        }

      </div>
    </div>
  );
}

export default App;
